import React from 'react'

class ToDoItem extends React.Component(props) {
    constructor() {
    	super()
    	this.state = {
    		data: this.props.item
    	}
    }

    render() {
        return (
            <div className='todo-item'>
				<input 
					type='checkbox' 
					checked={props.item.completed} 
					onChange={() => {console.log('changed!')}}
				/>
				<p>{props.item.text}</p>
			</div>
        )
    }
}

export default ToDoItem
